import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { AccountService } from '../../services/account/account.service';
import { Helpers } from '../../helpers/helpers';
import { TokenModel } from 'src/app/models/account/token-model';
import { LoginModel } from 'src/app/models/account/login-model';

@Component({
    selector: 'app-login',
    templateUrl: './login.component.html',
    providers: [AccountService]
})
export class LoginComponent{
    loginModel: LoginModel = new LoginModel();
    constructor(private helpers: Helpers, private router: Router, private accountService: AccountService){}

    login(): void{
        this.accountService.login(this.loginModel).subscribe((token: TokenModel) => {
            this.helpers.setToken(token);
            this.router.navigate(['/home'])
        })
    }
}
<div class="row">
    <div class="col-md-3"></div>
    <div class="col-md-6">
        <div class="form-group">
            <label>Name</label>
            <input type="text" placeholder="Enter your name!" class="form-control" [(ngModel)]="loginModel.name"/>
        </div>
        <br />
        <div class="form-group">
            <label>Password</label>
            <input type="password" class="form-control" placeholder="Enter your password!" [(ngModel)]="loginModel.password"/>
        </div>
        <br />
        <div class="form-group">
            <label>Remember me?</label>
            <input type="checkbox" class="form-control" placeholder="Remember me" [(ngModel)]="loginModel.rememberMe"/>
        </div>
        <br />
        <button class="btn btn-outline-primary" (click)="login()">Send</button>
    </div>
</div>
