import { Injectable } from '@angular/core';
import { Subject } from 'rxjs';
import { TokenModel } from '../models/account/token-model';
import { TokenViewModel } from '../models/account/token-viewmodel';

@Injectable({
  providedIn: 'root'
})
export class Helpers {
    private authenticationChanged = new Subject<boolean>();
    private readonly tokenStorageName: string = 'token';

    public isAuthenticated() : boolean {
        const localStorageToken = localStorage[this.tokenStorageName];
        const isExistedToken =  localStorageToken !== undefined && localStorageToken !== null && localStorageToken !== "undefined" && localStorageToken !== "null" && localStorageToken !== ''; 

        if(isExistedToken){
            const tokenModel = JSON.parse(localStorage[this.tokenStorageName]) as TokenModel;
            const today = new Date();
            const isActive = tokenModel.expirationDate >= today;

            if(!isActive){
                this.clearToken();
                return false;
            }

            return true;
        }
        
        return false; 
    }

    public isAuthenticationChanged(): any {
        return this.authenticationChanged.asObservable();
    }

    public getToken(): string|undefined {
        if(this.isAuthenticated())
            return undefined;
        const tokenModel = this.getTokenFromStorage();
        return tokenModel.token;
    }

    private getTokenFromStorage(): TokenViewModel|undefined {
        return JSON.parse(localStorage[this.tokenStorageName]) as TokenViewModel|undefined;
    }

    public setToken(data: TokenViewModel): void {
        const today = new Date();

        const tokenModel:TokenModel = new TokenModel();
        tokenModel.token = data.token;
        tokenModel.expirationDate.setDate(today.getDate() + data.expirationDays);

        this.setStorageToken(JSON.stringify(tokenModel));
    }

    public failToken(): void {
        this.clearToken();
    }

    public logout(): void {
        this.clearToken();
    }

    private clearToken(): void{
        this.setStorageToken(undefined);
    }

    private setStorageToken(value: any): void{
        localStorage[this.tokenStorageName] = value;
        this.authenticationChanged.next(this.isAuthenticated());
    }
}
