import { BaseService } from "../base.service";
import { Injectable } from '@angular/core';
import {TokenModel} from '../../models/account/token-model';
import { HttpClient } from "@angular/common/http";
import { AppConfig } from "src/app/config/config";
import { Helpers } from "src/app/helpers/helpers";
import { Observable, catchError } from "rxjs";
import { RegisterModel } from "src/app/models/account/register-model";
import { LoginModel } from "src/app/models/account/login-model";
import {AccountModel} from 'src/app/models/account/account-model';

@Injectable()
export class AccountService extends BaseService{
    private pathAPI = this.config.setting['PathAPI'];
    constructor(private httpClient: HttpClient, private config: AppConfig, helper: Helpers) {
         super(helper); 
    }
    
    private account(path:string, accountModel: AccountModel): Observable<TokenModel> {
        return this.httpClient.post<TokenModel>(this.pathAPI + path, accountModel, super.header()).pipe(
            catchError(super.handleError)
        );
    }

    login(loginModel: LoginModel): Observable<TokenModel> {
        return this.account('login', loginModel);
    }

    register(registerModel: RegisterModel): Observable<TokenModel> {
        return this.account('register', registerModel);
    }

    logout(): void {
        this.httpClient.post<TokenModel>(this.pathAPI + 'logout', super.header()).pipe(
            catchError(super.handleError)
        );
    }
}
