import { Router } from '@angular/router';
import { AccountService } from '../../services/account/account.service';
import { Helpers } from '../../helpers/helpers';
import { TokenViewModel } from 'src/app/models/account/token-viewmodel';
import { AccountModel } from 'src/app/models/account/account-model';
import { Observable } from "rxjs";

export abstract class AuthComponent{
    constructor(protected helpers: Helpers, protected router: Router, protected accountService: AccountService){}

    abstract getTokenViewModel() : Observable<TokenViewModel>;

    protected account(): void{
        this.getTokenViewModel().subscribe((token: TokenViewModel) => {
            this.helpers.setToken(token);
            this.router.navigate(['/home'])
        }, (error) => console.log('ERROR228: ' + error))
    }
}
