import { AfterViewInit, Component, ViewChild  } from '@angular/core';
import { MatDrawer } from '@angular/material/sidenav';
import { Subscription } from 'rxjs';
import { Helpers } from '../helpers/helpers';
import { startWith, delay } from 'rxjs/operators';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
})
export class AppComponent implements AfterViewInit{
  subscription: Subscription;
  authentication: boolean;
  readonly title = 'Notes!';
  
  constructor(private helpers: Helpers) {}

  ngAfterViewInit(): void {

    if (this.drawer) {
      // Check if the drawer is defined
      this.drawer.toggle(); // Toggle the drawer (open/close)
    }
    
    this.subscription = this.helpers.isAuthenticationChanged().pipe(
      startWith(this.helpers.isAuthenticated()),
      delay(0)
    ).subscribe((value: boolean) => this.authentication = value);
  }


  ngOnDestroy(){
    this.subscription.unsubscribe();
  }

  @ViewChild('drawer') 
  drawer: MatDrawer;

  
  toggleMenu(): void {
    console.log('this.drawer: ' + this.drawer)
    if (this.drawer) {
      this.drawer.toggle(); 
    }
  }
}

<div class="container-fluid">
  <header class="navbar navbar-expand-lg navbar-dark bg-dark">
    <div class="container">
      <button type="button" mat-button class="btn btn-light" (click)="drawer.toggle()">
        <i class="fas fa-bars"></i> Menu
      </button>
      <a class="navbar-brand" href="">{{title}}</a>
      <button mat-button [routerLink]="['./logout']" class="btn btn-dark text-white text-right" >Logout</button>
    </div>
  </header>
  <div class="row">
    <div class="col-md-2">
      <mat-drawer #drawer mode="side" class="left-panel">
        <app-left-panel-layout [isAuthenticated]="authentication" />
      </mat-drawer>
    </div>
    <div class="col-md-10">
      <router-outlet />
    </div>
  </div>
</div>
