import { BaseService } from "../base.service";
import { Injectable } from '@angular/core';
import { TokenModel } from '../../models/account/token-model';
import { HttpClient } from "@angular/common/http";
import { Helpers } from "src/app/helpers/helpers";
import { Observable, catchError } from "rxjs";
import { RegisterModel } from "src/app/models/account/register-model";
import { LoginModel } from "src/app/models/account/login-model";
import { AccountModel } from 'src/app/models/account/account-model';
import { TokenViewModel } from "src/app/models/account/token-viewmodel";

@Injectable()
export class AccountService extends BaseService{
    override controllerName: string = 'account';
    constructor(private httpClient: HttpClient, helper: Helpers) {
         super(helper); 
    }
    
    private account(path:string, accountModel: AccountModel): Observable<TokenViewModel> {
        return this.httpClient.post<TokenViewModel>(`${super.pathBase} ${path}`, accountModel, super.header()).pipe(
            catchError(super.handleError)     
        );
    }

    login(loginModel: LoginModel): Observable<TokenViewModel> {
        return this.account('login', loginModel);
    }

    register(registerModel: RegisterModel): Observable<TokenViewModel> {
        return this.account('register', registerModel);
    }

    logout(): void {
        this.httpClient.post(super.pathBase + 'logout', super.header()).pipe(
            catchError(super.handleError)
        );
    }
}
