import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { AuthService } from '../../services/auth/auth.service';
import { Helpers } from '../../helpers/helpers';
import { LoginModel } from 'src/app/models/auth/login-model';
import { TokenViewModel } from 'src/app/models/auth/token-viewmodel';
import { AuthComponent } from './auth.component';
import { Observable } from "rxjs";

@Component({
    selector: 'app-login',
    templateUrl: './login.component.html',
    providers: [AuthService]
})
export class LoginComponent extends AuthComponent{
    loginModel: LoginModel = new LoginModel();
    constructor(helpers: Helpers, router: Router,  authService: AuthService){
        super(helpers, router, authService);
    }

    getTokenViewModel() : Observable<TokenViewModel> {
        return this.authService.login(this.loginModel);
    }
}
<div class="row">
    <div class="col-md-3"></div>
    <div class="col-md-6">
        <div class="form-group">
            <label>Name</label>
            <input type="text" placeholder="Enter your name!" class="form-control" [(ngModel)]="loginModel.name"/>
        </div>
        <br />
        <div class="form-group">
            <label>Password</label>
            <input type="password" class="form-control" placeholder="Enter your password!" [(ngModel)]="loginModel.password"/>
        </div>
        <br />
        <div class="form-check">
            <label class="form-check-label">Remember me?</label>
            <input type="checkbox" class="form-check-input" placeholder="Remember me" [(ngModel)]="loginModel.rememberMe"/>
        </div>
        <br />
        <button class="btn btn-outline-primary" (click)="account()">Send</button>
    </div>
</div>
